package l1data

import (
	"math/big"

	"github.com/ethereum/go-ethereum/core/types"
)

// These types were originally generated by geth's abigen tool.

type LogStateUpdate struct {
	GlobalRoot  *big.Int
	BlockNumber *big.Int
	// BlockHash was not included in state updates before v0.12.3 (May 2023 for mainnet).
	BlockHash *big.Int
	Raw       types.Log
}

func (l *LogStateUpdate) ABIName() string {
	return "LogStateUpdate"
}

type LogStateTransitionFact struct {
	StateTransitionFact [32]byte
	Raw                 types.Log
}

type LogMemoryPageFactContinuous struct {
	FactHash   [32]byte
	MemoryHash *big.Int
	Prod       *big.Int
	Raw        types.Log
}

type LogMemoryPagesHashes struct {
	ProgramOutputFact [32]byte
	PagesHashes       [][32]byte
	Raw               types.Log
}
